const SHORT_LEGS = "short legs"
const MEDIUM_LEGS = "medium legs"
const LONG_LEGS = "long legs"
const LEGS = [SHORT_LEGS, MEDIUM_LEGS, LONG_LEGS]

const THIN = "thin"
const MEDIUM = "medium"
const BULKY = "bulky"
const SIZE = [THIN, MEDIUM, BULKY]

const SHORT_NECK = "short neck"
const MEDIUM_NECK = "medium neck"
const LONG_NECK = "long neck"
const NECK = [SHORT_NECK, MEDIUM_NECK, LONG_NECK]

const NO_HAIR = "no hair"
const SHORT_HAIR = "short hair"
const MEDIUM_HAIR = "medium hair"
const LONG_HAIR = "long hair"
const HAIR = [NO_HAIR, SHORT_HAIR, MEDIUM_HAIR, LONG_HAIR]

const STRIPES = "stripes"
const NO_STRIPES = "no stripes"
const CAMO = [STRIPES, NO_STRIPES]

class Traits {
    constructor(legs, size, neck, hair, camo) {
        this.legs = legs;
        this.size = size;
        this.neck = neck;
        this.hair = hair;
        this.camo = camo;
    }
};

function createRandomOffspring() {
    return new Traits(
        randomElement(LEGS),
        randomElement(SIZE),
        randomElement(NECK),
        randomElement(HAIR),
        randomElement(CAMO)
    );
};

function randomElement(array) {
    return array[Math.floor(Math.random() * array.length)];
};

const COLD = "cold"
const HOT = "hot"
const PREDATORS = "predators"
const TALL_PLANTS = "tall plants"
const VIRUS = "virus"
const VOLCANO = "volcano"

const CATASTROPHES = [COLD, HOT, PREDATORS, TALL_PLANTS, VIRUS, VOLCANO]

const SURVIVAL_CHANCE = {
    [COLD]: {
        legs: { [SHORT_LEGS]: 20, [MEDIUM_LEGS]: 10, [LONG_LEGS]: 0 },
        hair: { [NO_HAIR]: 0, [SHORT_HAIR]: 10, [MEDIUM_HAIR]: 15, [LONG_HAIR]: 20 },
        size: { [THIN]: -10, [MEDIUM]: 10, [BULKY]: 20 },
        neck: { [SHORT_NECK]: 20, [MEDIUM_NECK]: 10, [LONG_NECK]: 0 }
    },

    [HOT]: {
        legs: { [SHORT_LEGS]: 0, [MEDIUM_LEGS]: 10, [LONG_LEGS]: 20 },
        hair: { [NO_HAIR]: 20, [SHORT_HAIR]: 10, [MEDIUM_HAIR]: 0, [LONG_HAIR]: -10 },
        size: { [THIN]: 20, [MEDIUM]: 10, [BULKY]: 0 },
        neck: { [SHORT_NECK]: 0, [MEDIUM_NECK]: 10, [LONG_NECK]: 20 }
    },

    [PREDATORS]: {
        legs: { [SHORT_LEGS]: -10, [MEDIUM_LEGS]: 10, [LONG_LEGS]: 30 },
        size: { [THIN]: 20, [MEDIUM]: 10, [BULKY]: 0 },
        camo: { [STRIPES]: 30, [NO_STRIPES]: 0 }
    },

    [TALL_PLANTS]: {
        legs: { [SHORT_LEGS]: 0, [MEDIUM_LEGS]: 20, [LONG_LEGS]: 40 },
        neck: { [SHORT_NECK]: 0, [MEDIUM_NECK]: 20, [LONG_NECK]: 40 }
    },

    // 60% max survival
    [VIRUS]: {
        legs: { [SHORT_LEGS]: 20, [MEDIUM_LEGS]: 10, [LONG_LEGS]: 0 },
        size: { [THIN]: 20, [MEDIUM]: 10, [BULKY]: 0 },
        neck: { [SHORT_NECK]: 0, [MEDIUM_NECK]: 10, [LONG_NECK]: 20 }
    },

    // 60% max survival
    [VOLCANO]: {
        legs: { [SHORT_LEGS]: 20, [MEDIUM_LEGS]: 10, [LONG_LEGS]: 0 },
        size: { [THIN]: 20, [MEDIUM]: 10, [BULKY]: 0 },
        neck: { [SHORT_NECK]: 20, [MEDIUM_NECK]: 10, [LONG_NECK]: 0 }
    }
}

export default {
    SHORT_LEGS,
    MEDIUM_LEGS,
    LONG_LEGS,
    LEGS,
    THIN,
    MEDIUM,
    BULKY,
    SIZE,
    SHORT_NECK,
    MEDIUM_NECK,
    LONG_NECK,
    NECK,
    NO_HAIR,
    SHORT_HAIR,
    MEDIUM_HAIR,
    LONG_HAIR,
    HAIR,
    NO_STRIPES,
    STRIPES,
    CAMO,
    Traits,
    createRandomOffspring,
    randomElement,
    COLD,
    HOT,
    PREDATORS,
    VIRUS,
    VOLCANO,
    CATASTROPHES,
    SURVIVAL_CHANCE,
};
